mapper.select:
	select * from pre_purchase where tenant
	= #{tenant}
	
		and id = #{id}
	
	
		and last_update_user = #{lastUpdateUser}
	
	
		and last_update_date = #{lastUpdateDate}
	
	
		and status = #{status}
	
	
		and name like '%${entity.supplierName}%'
    
	limit 1

	select * from suppliers where tenant
	= #{tenant}
	
		and id = #{id}
	
	
		and last_update_user = #{lastUpdateUser}
	
	
		and last_update_date = #{lastUpdateDate}
	
	
		and status = #{status}
	
	
		and name like '%${entity.supplierName}%'

	select * from Suppliers where tenant = #{entity.tenant}
	
		and name like '%${entity.supplierName}%'
    
	limit
	#{offset},#{limit}

	select * from suppliers where tenant
	= #{tenant}
	
		and id = #{id}
	
	
		and last_update_user = #{lastUpdateUser}
	
	
		and last_update_date = #{lastUpdateDate}
	
	
		and status = #{status}
	
	
		and name like '%${entity.supplierName}%'

	select * from Suppliers where tenant = #{entity.tenant}
	
		and name like '%${entity.supplierName}%'
    
	limit
	#{offset},#{limit}
